def f(v, k, dep):
    n = len(v)
    p = None
    for i in range(n):
        if abs(v[i] - v[(i + 1) % n]) > k:
            p = i
    if p == None:
        return []
    if dep == 0:
        return None
    for i in [p, (p + 1) % n]:
