#include <stdio.h>
#include <stdlib.h>
#define min(a,b) a<b?a:b
const int N = 2e6 + 10;
typedef long long LL;
const int big = 1e9;
int ans;
int f[2000010];
int cnt = 0;
void swap(int* a, int* b)
{
    int t;
    t = *a;
    *a = *b;
    *b = t;
}
void init()
{
    for (int i = 0; i <= 1e6; i++)
    {
        if (f[i] == 0)
        {
            f[i] = i + cnt;
            f[i + cnt] = i;
            cnt++;
        }
    }
}
int main()
{
    int T;
    scanf("%d", &T);
    init();
    while (T--)
    {
        int x, y;
        scanf("%d%d", &x, &y);
        if (x > y)swap(&x, &y);
        ans = (y - x) * 2 + 1;
        if (f[x] == y)
        {
            printf("%lld\n", (LL)2 * big - (y - x) * 2);
            continue;
        }
        if (f[x] < y) ans = min(ans, 2 * abs(f[x] - x) + 1);
        if (f[y] < x)ans = min(ans, 2 * abs(y - f[y]) + 1);
        printf("%d\n", 2 * big - ans);
    }
    return 0;
}
