def check(s):
    res = 0
    n = len(s)
    dp = [[0,0,0] for _ in range(n)]
    for i in range(n):
        dp[i][2] = i
        if s[i]=='a':
            dp[i][0]=dp[i-1][0]+1
            dp[i][1] = dp[i-1][1]
        elif s[i]=='c':
            dp[i][1]=dp[i-1][1]+1
            dp[i][0]=dp[i-1][0]
        else:
            dp[i][0] = dp[i-1][0]
            dp[i][1] = dp[i-1][1]
        for k in range(i):
            diff1 = dp[i][0] - dp[k][0] 
            diff2 = dp[i][1] - dp[k][1]
            if diff1 == diff2:
                if diff1%2==0:
                    res += (i-dp[k][2])
                else:
                    res -= (i-dp[k][2])-1
#         print(dp)
    return res

while True:
    try:
        s = input()
        print(check(s))
    except:
        break

